import React, {useState, useEffect} from "react";

const App = () => {
  const[quotes, setQuote] = useState("");  
  const getQuote = () => {
    fetch("https://type.fit/api/quotes")
      .then((res) => res.json())
      .then((data) => {
        let randomNumb = Math.floor(Math.random() * data.length);
        setQuote(data[randomNumb]); //get the random quote 
      });
  }

  useEffect(() => {  //when you refresh gets new quote
    getQuote();
  }, []);

  return (
    <div className="App">
      <div className = "quote">
        <p>{quotes.text}</p>
        <p>{quotes.author}</p>
        <div className = "buttonContainer">
          <button className = "button">Get Quote</button>
          <a href = "" className = "button"> Tweet </a>  
        </div>
      </div>
    </div>
  );
};

export default App;
